import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { useAuth } from '../contexts/AuthContext';
import bookingService from '../services/bookingService';

// Achievement definitions (matches original app.js)
const ACHIEVEMENTS = [
  {
    id: 'first_booking',
    name: 'First Steps',
    description: 'Complete your first booking',
    icon: 'üéØ',
    xp: 50
  },
  {
    id: 'early_bird',
    name: 'Early Bird',
    description: 'Book a seat before 8 AM',
    icon: 'üåÖ',
    xp: 100
  },
  {
    id: 'night_owl',
    name: 'Night Owl',
    description: 'Book a seat after 8 PM',
    icon: 'ü¶â',
    xp: 100
  },
  {
    id: 'week_warrior',
    name: 'Week Warrior',
    description: 'Book seats for 7 consecutive days',
    icon: 'üìÖ',
    xp: 200
  },
  {
    id: 'marathon_session',
    name: 'Marathon Session',
    description: 'Complete a 4+ hour session',
    icon: '‚è±Ô∏è',
    xp: 150
  },
  {
    id: 'perfect_week',
    name: 'Perfect Week',
    description: 'Zero no-shows in a week',
    icon: '‚ú®',
    xp: 250
  },
  {
    id: 'century_club',
    name: 'Century Club',
    description: 'Complete 100 bookings',
    icon: 'üíØ',
    xp: 500
  },
  {
    id: 'focus_master',
    name: 'Focus Master',
    description: 'Accumulate 100+ hours of focus time',
    icon: 'üßò',
    xp: 1000
  },
  {
    id: 'level_10',
    name: 'Level 10 Legend',
    description: 'Reach Level 10',
    icon: 'üëë',
    xp: 500
  },
  {
    id: 'pod_lover',
    name: 'Pod Enthusiast',
    description: 'Book 20 pod seats',
    icon: 'üé™',
    xp: 150
  },
  {
    id: 'standing_desk',
    name: 'Standing Strong',
    description: 'Book 15 standing desks',
    icon: 'üö∂',
    xp: 150
  },
  {
    id: 'streak_7',
    name: '7 Day Streak',
    description: 'Book and complete 7 days in a row',
    icon: 'üî•',
    xp: 300
  }
];

const Profile = () => {
  const { currentUser, userProfile } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [rank, setRank] = useState(1);

  useEffect(() => {
    loadProfileData();
  }, [currentUser]);

  const loadProfileData = async () => {
    try {
      setLoading(true);
      // Load booking history
      const userBookings = await bookingService.getUserBookings(currentUser.uid);
      setBookings(userBookings);

      // Calculate rank (simplified - in production, would query all users)
      setRank(userProfile?.stats?.level || 1);
    } catch (error) {
      console.error('Error loading profile data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDuration = (minutes) => {
    if (!minutes) return '0h 0m';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getMemberSince = () => {
    if (!userProfile?.createdAt) return '2026';

    try {
      const date = userProfile.createdAt.toDate 
        ? userProfile.createdAt.toDate() 
        : new Date(userProfile.createdAt.seconds * 1000);
      return date.getFullYear();
    } catch (error) {
      return '2026';
    }
  };

  const exportData = () => {
    const exportData = {
      profile: {
        name: userProfile.name,
        email: userProfile.email,
        department: userProfile.department || '',
        rollNo: userProfile.rollNo || '',
      },
      stats: userProfile.stats,
      achievements: userProfile.achievements || [],
      bookings: bookings.map(b => ({
        seatId: b.seatId?.id || b.seatId,
        startTime: b.startTime?.toDate?.() || new Date(b.startTime?.seconds * 1000),
        endTime: b.endTime?.toDate?.() || new Date(b.endTime?.seconds * 1000),
        status: b.status,
        duration: b.duration,
        xpEarned: b.xpEarned
      })),
      exportedAt: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `bookmyseat_profile_${currentUser.uid}.json`;
    link.click();
    URL.revokeObjectURL(url);

    alert('Profile data exported successfully!');
  };

  const getUnlockedAchievements = () => {
    const userAchievementIds = userProfile?.achievements || [];
    return ACHIEVEMENTS.filter(a => userAchievementIds.includes(a.id));
  };

  const getLockedAchievements = () => {
    const userAchievementIds = userProfile?.achievements || [];
    return ACHIEVEMENTS.filter(a => !userAchievementIds.includes(a.id));
  };

  const getBadgeClass = (status) => {
    switch (status) {
      case 'completed':
        return 'badge-success';
      case 'active':
        return 'badge-primary';
      case 'pending':
        return 'badge-gold';
      case 'cancelled':
      case 'no_show':
        return 'badge-primary';
      default:
        return 'badge-gold';
    }
  };

  const unlockedAchievements = getUnlockedAchievements();
  const lockedAchievements = getLockedAchievements();

  if (loading) {
    return (
      <>
        <Navbar isAuthenticated={true} />
        <div className="container" style={{ paddingTop: '120px', textAlign: 'center' }}>
          <div className="loader-spinner"></div>
          <p className="loader-text">Loading profile...</p>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar isAuthenticated={true} />

      <div className="container" style={{ paddingTop: '120px', paddingBottom: '60px' }}>
        {/* Profile Header */}
        <div className="glass-panel" style={{ marginBottom: '40px' }}>
          <div className="flex justify-between items-start flex-wrap gap-4">
            <div>
              <h1 style={{ marginBottom: '8px' }}>{userProfile?.name || 'User'}</h1>
              <p className="text-muted">{userProfile?.email || 'email@example.com'}</p>
              <div className="flex gap-2" style={{ marginTop: '16px', flexWrap: 'wrap' }}>
                <span className="badge badge-gold">
                  Level {userProfile?.stats?.level || 1}
                </span>
                <span className="badge badge-primary">
                  Rank #{rank}
                </span>
                <span className="badge badge-success">
                  Member since {getMemberSince()}
                </span>
              </div>
            </div>
            <button onClick={exportData} className="btn btn-secondary">
              üì• Export Data
            </button>
          </div>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-4 gap-6" style={{ marginBottom: '40px' }}>
          <div className="glass-panel text-center">
            <div className="stat-value">{(userProfile?.stats?.xp || 0).toLocaleString()}</div>
            <div className="stat-label">Total XP</div>
          </div>
          <div className="glass-panel text-center">
            <div className="stat-value text-teal">
              {formatDuration(userProfile?.stats?.totalMinutes || 0)}
            </div>
            <div className="stat-label">Focus Time</div>
          </div>
          <div className="glass-panel text-center">
            <div className="stat-value text-gold">
              {userProfile?.stats?.totalBookings || 0}
            </div>
            <div className="stat-label">Total Bookings</div>
          </div>
          <div className="glass-panel text-center">
            <div className="stat-value text-secondary">
              {(userProfile?.achievements || []).length}
            </div>
            <div className="stat-label">Achievements</div>
          </div>
        </div>

        {/* Achievements Section */}
        <div className="glass-panel" style={{ marginBottom: '40px' }}>
          <h2 style={{ marginBottom: '24px' }}>üèÜ Achievements</h2>

          {/* Unlocked Achievements */}
          <h3 style={{ fontSize: '1rem', marginBottom: '16px', color: 'var(--text-muted)' }}>
            Unlocked
          </h3>
          {unlockedAchievements.length > 0 ? (
            <div className="grid grid-cols-3 gap-4" style={{ marginBottom: '32px' }}>
              {unlockedAchievements.map((achievement) => (
                <div key={achievement.id} className="card" style={{ textAlign: 'center' }}>
                  <div style={{ fontSize: '3rem', marginBottom: '12px' }}>
                    {achievement.icon}
                  </div>
                  <h4 style={{ marginBottom: '8px' }}>{achievement.name}</h4>
                  <p className="text-muted" style={{ fontSize: '0.875rem', marginBottom: '8px' }}>
                    {achievement.description}
                  </p>
                  <span className="badge badge-gold">+{achievement.xp} XP</span>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-muted" style={{ marginBottom: '32px' }}>
              No achievements unlocked yet. Start booking seats to earn achievements!
            </p>
          )}

          {/* Locked Achievements */}
          <h3 style={{ fontSize: '1rem', marginBottom: '16px', color: 'var(--text-muted)' }}>
            Locked
          </h3>
          {lockedAchievements.length > 0 ? (
            <div className="grid grid-cols-3 gap-4">
              {lockedAchievements.map((achievement) => (
                <div
                  key={achievement.id}
                  className="card"
                  style={{ textAlign: 'center', opacity: 0.5 }}
                >
                  <div
                    style={{
                      fontSize: '3rem',
                      marginBottom: '12px',
                      filter: 'grayscale(100%)'
                    }}
                  >
                    {achievement.icon}
                  </div>
                  <h4 style={{ marginBottom: '8px' }}>üîí {achievement.name}</h4>
                  <p className="text-muted" style={{ fontSize: '0.875rem', marginBottom: '8px' }}>
                    {achievement.description}
                  </p>
                  <span className="badge badge-primary">+{achievement.xp} XP</span>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-muted">All achievements unlocked! üéâ</p>
          )}
        </div>

        {/* Booking History */}
        <div className="glass-panel">
          <h2 style={{ marginBottom: '24px' }}>üìö Booking History</h2>
          <div>
            {bookings.length > 0 ? (
              bookings.map((booking) => {
                // Extract seat ID properly
                const seatId = typeof booking.seatId === 'object'
                  ? booking.seatId.id || booking.seatId.path?.split('/').pop() || 'Unknown'
                  : booking.seatId;

                // Format times
                const startTime = booking.startTime?.toDate
                  ? booking.startTime.toDate()
                  : new Date(booking.startTime?.seconds * 1000);

                const endTime = booking.endTime?.toDate
                  ? booking.endTime.toDate()
                  : new Date(booking.endTime?.seconds * 1000);

                const timeSlot = `${startTime.toLocaleTimeString('en-US', {
                  hour: '2-digit',
                  minute: '2-digit'
                })} - ${endTime.toLocaleTimeString('en-US', {
                  hour: '2-digit',
                  minute: '2-digit'
                })}`;

                const badgeClass = getBadgeClass(booking.status);

                return (
                  <div
                    key={booking.id}
                    className="flex justify-between items-center"
                    style={{
                      padding: '16px',
                      borderBottom: '1px solid var(--border-color)'
                    }}
                  >
                    <div>
                      <p style={{ fontWeight: 600, marginBottom: '4px' }}>
                        Seat {seatId}
                        <span className={`badge ${badgeClass}`} style={{ marginLeft: '8px' }}>
                          {booking.status}
                        </span>
                      </p>
                      <p className="text-muted" style={{ fontSize: '0.875rem' }}>
                        {startTime.toLocaleDateString()} {timeSlot}
                        {booking.duration && ` ‚Ä¢ Duration: ${formatDuration(booking.duration)}`}
                      </p>
                    </div>
                    <div className="text-right">
                      {booking.xpEarned && (
                        <p
                          className="text-gold"
                          style={{ fontWeight: 700, fontSize: '1.125rem' }}
                        >
                          +{booking.xpEarned} XP
                        </p>
                      )}
                    </div>
                  </div>
                );
              })
            ) : (
              <p className="text-muted text-center">
                No bookings yet. Visit the dashboard to book your first seat!
              </p>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Profile;
